		dammidist = dammidist + 70.
		lopa = lopa + 1
		dammi = nil
		ef = nil
	end
	SaveUnitHandle(udg_hash, h, 37, caster)
	SaveReal(udg_hash, h, 38, damage)
	SaveInteger(udg_hash, h, 39, wait)
	SaveReal(udg_hash, h, 40, angle)
	SaveGroupHandle(udg_hash, h, 41, gdamage)
