		SaveInteger(udg_hash, h, 2, bonusstr)
		SaveInteger(udg_hash, h, 3, bonusagi)
		SaveInteger(udg_hash, h, 4, bonusint)
		SaveEffectHandle(udg_hash, h, 5, ef)
