		TimerStart(tsp, 1., false, Trig_Holes_Of_Grave_Diggers_AttackBase_Actions)
		spider = nil
		tsp = nil
	else
		dammi = LoadUnitHandle(udg_hash, h, 4)
		ef = LoadEffectHandle(udg_hash, h, 5)
		DestroyEffect(ef)
		ef = nil
		KillUnit(dammi)
		dammi = nil
		KillUnit(hole)
		hole = nil
		FlushChildHashtable(udg_hash, h)
		DestroyTimer(t)
		t = nil
	end
	t = nil
	hole = nil
	p = nil
end

