		tx = GetLocationX(loc)
		ty = GetLocationY(loc)
	else
		tx = GetUnitX(target)
		ty = GetUnitY(target)
	end
	SetUnitAnimation(caster, "stand")
	SetUnitPathing(caster, false)
	SetUnitTimeScale(caster, 4.)
	PauseUnit(caster, true)
