	SetUnitFacing(caster, angle)
	SaveUnitHandle(udg_hash, h, 1, caster)
	SaveReal(udg_hash, h, 3, damage)
	SaveInteger(udg_hash, h, 4, 66)
	SaveInteger(udg_hash, h, 5, R2I(dist / 25.))
	SaveReal(udg_hash, h, 6, angle)
