			Picked = nil
			DestroyEffect(AddSpecialEffect("Abilities\\Weapons\\PhoenixMissile\\Phoenix_Missile.mdl", xc + 100. * Cos(anglejump * bj_DEGTORAD), yc + 100. * Sin(anglejump * bj_DEGTORAD)))
