			DestroyEffect(AddSpecialEffect("Abilities\\Weapons\\Bolt\\BoltImpact.mdl", xt, yt))
			SaveLightningHandle(udg_hash, hl, lop, l)
			l = nil
			lop = lop + 1
			RemoveLocation(locz)
			locz = nil
		end
		l = nil
		locz = Location(xt, yt)
