			GroupRemoveUnit(g, target)
			RemoveLocation(locz)
			locz = nil
			l[lop] = nil
			target = nil
			lop = lop + 1
		end
		SaveInteger(udg_hash, hl, 20, check)
		SaveUnitHandle(udg_hash, hl, 21, caster)
