	end
	SaveGroupHandle(udg_hash, h, 1, gheal)
	SaveInteger(udg_hash, h, 2, adddamage)
	SaveInteger(udg_hash, h, 3, addarmor)
	SaveInteger(udg_hash, h, 5, liferegen)
