		SaveEffectHandle(udg_hash, hcaster, FourCC('r'), hammeref)
		hammeref = nil
		hammeref = AddSpecialEffectTarget("Effects\\LifeHigh.mdx", caster, "weapon")
		SaveEffectHandle(udg_hash, hcaster, FourCC('s'), hammeref)
		hammeref = nil
	end
